    this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleSubmit(event) {


    body = {
      Email: this.state.email,
      Password: this.state.password,

    }
   /* if(body.name=='null' && (!body.password)){
      alert('please enter the user name and password');
    }
    */
    if(!body.Email){
       alert('please enter the email');
    }
    else if(!body.Password){
      alert('please enter the password');
    }
    else {
    //console.log(body)
    console.log(body.Password)
    console.log(body.Email)

    const url = "http://localhost:9000/login?Email="+body.Email+"& Password="+body.Password;

    console.log(url)

    let headers = new Headers();

    headers.append('Content-Type','application/json');
    headers.append('Accept','application/json');

    headers.append('Access-Control-Allow-origin',url);

    headers.append('Access-Control-Allow-Credentials','true');

    headers.append('PUT','GET');
    this.history=createBrowserHistory();
    fetch(url, {
            headers:headers,
            method: 'PUT',
            body: JSON.stringify(body)
          })
          .then(response => response.json())
          .then(contents => {console.log(contents);


          localStorage.setItem("AccessToken",contents.token);
          console.log(localStorage.getItem("AccessToken"))


          let path=`logged in`
          this.props.history.push('/user-home');


      })
          .catch(()=> console.log("can't access" + url + "response. "))

      }

    } 

  validateForm() {
    return this.state.name.length > 0 && this.state.password.length > 5;
  }

  handlePasswordChange (event) {
    this.setState({

      password: event.target.value

    });
    console.log(event.target.value)
  }


  handleEmailChange(event) {
    this.setState({ email: event.target.value})
    console.log(event.target.value)
  }

    render() {
        return (<div className="bg">
           <nav className="navbar navbar-expand-lg navbar-light fixed-top">
        <div className="container">
         {/* <Link className="navbar-brand" to={"/Login"}>Know</Link> */}
          <div className="collapse navbar-collapse" id="navbarTogglerDemo02"> 
          <div className="navbar-nav mr-auto">
          <Link className="nav-link" ><b>CITIZENS ENGAGE</b></Link>
          </div>
            <ul className="navbar-nav ml-auto">

            <li className="nav-item">
                <Link className="nav-link" to={"/Home"}>Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to={"/Login"}>Login</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to={"/sign-up"}>Sign up</Link>
              </li>

            </ul>
          </div>
        </div>
      </nav>

            <br></br><br/>
            <br/>
            <br/>
            <br/>
            <br/>

            <div className="auth-wrapper">
            <div className="auth-inner">

            <form>
                <h3>Login</h3>

                <div className="form-group">
                    <label>Email address</label>
                    <input type="text" name="email" id="exampleEmail" className="form-control" placeholder="Enter email"
                    onChange = {this.handleEmailChange} />
                </div>

                <div className="form-group">
                    <label>Password</label>
                    <input type="password" name="password" id="examplePassword" className="form-control" placeholder="Enter password" 
                    onChange = {this.handlePasswordChange}/>
                </div>

                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>

                <button type="submit" className="btn btn-primary btn-block" onClick={this.handleSubmit}>Login</button>
                <p className="forgot-password text-right">
                    Forgot <a href="#">password?</a>
                </p>



                <p className="forgot-password text-right">
                    If not registered <a href="/sign-up">Signup?</a>
                </p>



            </form>
            </div>
            </div>
            </div>
        );
    }
}
export default withRouter(Login);
