import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './UserHome.css'

class PastBids extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'items' : [],
            buyerid: this.props.id,
        };
    }
    
    componentDidMount() {
        fetch('http://localhost:9000/getPastBids/'+this.state.buyerid)
        .then(response => response.json())
        .then(data => this.setState({ 'items' : data }));
    }

    render(){
        return (
            <div class = "auth-inner-half">
            <Row>
            <h1>Bids Closing Soon</h1><hr/>
            <button type="submit" className="btn btn-primary btn-lg float-right ml-auto" onClick={() => {window.location.href = "/allCropsBuyer/"+ this.state.id}}>VIEW ALL CROPS</button>
            </Row><hr/>
            <Row>
                <Col xs="1">CROP</Col><Col xs="1"></Col><Col xs="2">AREA</Col><Col xs="2">LOCATION</Col><Col xs="1"></Col><Col xs="2">PRICE</Col><Col xs="3">BID</Col>
            </Row><hr/>
                <ul>
                   {this.state.items.map(function(item){
                       return(
                           <div key={item.id} className = "cropList">
                                <h2>{item.name},{item.area},{item.location},{item.price}</h2>
                                <button className="btn btn-warning btn-block" onClick={() => this.handleSubmit(item.id).bind(this)}>Bid</button>
                           </div>
                       )
                   })}
               </ul>
            </div>
        );
    }
}

export default PastBids;
