import React, { Component } from "react";
import logo from '../images/userhome.jpg';
import '../images/bgimage.css';
import './UserHome.css'
import Nav from './nav.js';
import { Container } from "react-bootstrap";
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

export default class CropsIn extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'items' : [],
            location : this.props.match.params.id
        };
    }

    componentDidMount() {
        const url = 'http://localhost:9000/cropsIn/'+this.state.location
        console.log(this.state.location)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
        })
        .then(response => response.json()) 
        .then(response => this.setState({ 'items' : response}));           
    }

    render(){
        return (
            <div>
            <Nav/>
            <div className = "userhomebg">
            <div class = "recentbids">
                Crops Found in {this.state.location}
                <ul>
                   {this.state.items.map(function(item){
                       return(
                           <div key={item.id}>
                                <h2>{item.name},{item.area},{item.price}</h2>
                                <button className="btn btn-primary btn-block" onClick="http://localhost:9000/getfarmercrops/+item.id">Bid</button>
                           </div>
                       )
                   })}
               </ul>
            </div>
            </div>
            </div>
        );
    }
}
