import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './UserHome.css'
import Select from 'react-select';

import { Note } from '../styled-components';
//import { GetLocations } from '/Users/macadmin/Downloads/f-m/src/components/home/getLocations.js';



export default class ListLocations extends Component {
  selectRef: ElementRef<*>;
  focus = () => {
    console.log(this.selectRef);
    this.selectRef.focus();
  };
  blur = () => this.selectRef.blur();
  onSelectRef = ref => {
    console.log(ref);
    this.selectRef = ref;
  };




  constructor(props) {
        super(props);
        this.state = {
            'items' : []
        };
    }

    componentDidMount() {
        const url = 'http://localhost:9000/getlocations/'
        console.log(this.props.fid)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
        })
        .then(response => response.json()) 
        .then(response => this.setState({ 'items' : response}));   
        locationOptions =  this.state.items;       
    }

    render(){
        return (
            // <div>
            //     <NavDropdown title={window.localStorage.getItem("username")} id="basic-nav-dropdown">
            //        {this.state.items.map(function(item){
            //            return(
            //             <NavDropdown.Item>{item.location}</NavDropdown.Item>
            //            )
            //        })}
            //    </NavDropdown>
            // </div>


          <Fragment>  
        <h4>Select</h4>
        <Select
          ref={ref => {
            this.selectRef = ref;
          }}
          defaultValue="Select Location"
          name="colors"
          options={colourOptions}/>
        <Note Tag="label">
          <button onClick={this.focus} id="cypress-single__clearable-checkbox">Focus</button>
        </Note>
        <Note Tag="label">
          <button onClick={this.blur} id="cypress-single__clearable-checkbox">Blur</button>
        </Note>
        </Fragment>


        );
    }
}
