import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import {
Container
} from 'react-bootstrap'
import Row from 'react-bootstrap/Row'
import './UserHome.css'

var cname
var clocation
var cprice


class BidsToBuy extends Component {
    constructor(props) {
        super(props);
        this.state = {
        cid : ''
        };
    }

    componentDidMount() {
        const url = 'http://localhost:9000/getcrops'
        console.log(this.props.fid)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
            method: 'GET',
            body: JSON.stringify(body)
        })
        .then(response => {if(response.ok){
            result = response.arrayBuffer()
            .then(result => {
              this.state.cid = result.id
              cname = result.name
              clocation = result.location
              cprice = result.price
              console.log(this.state)

            })  
        } 
        })
        .catch(() => console.log("can't access" + url + "response."))        
    }

    handleSubmit(event){
        window.location.href  = "/bidding/"+this.state.cid;
    }

    render(){
        return (
            <div class = "recentbids">
                <Container>
                    <Row>
                        {this.state.data}
                    </Row>
                    <Row>
                       {cname}
                       {clocation}
                       {cprice}
                        Crop 1 &nbsp;
                         <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>

                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 2 &nbsp;
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 3 &nbsp;
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 4 &nbsp;
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 5 &nbsp;
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>

                </Container>
            </div>
        );
    }
}

export default BidsToBuy;
