import React, { Component } from "react";
import logo from '../images/userhome.jpg';
import '../images/bgimage.css';
import './UserHome.css'
import Nav from './nav.js';
import { Container } from "react-bootstrap";
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'
import CropProfile from './CropProfile'

var body;
var profile;

export default class Bids extends Component{
    constructor(props) {
        super(props);
        this.state = {
            cid : this.props.match.params.cid,
            fid : this.props.match.params.fid,
            profile : false,
            'items' : [],
        };
    }


    componentDidMount(){
            const url = 'http://localhost:9000/getCropBids/'+this.state.cid
            console.log("ON CLICK BIDDING.")
            console.log(body)
            let headers = new Headers();

            headers.append('Content-Type', 'application/json');
            headers.append('Accept', 'application/json');

            headers.append('Access-Control-Allow-origin', url);
            headers.append('Access-Control-Allow-Credentials', 'true');

            headers.append('POST', 'GET', 'DELETE');

            fetch(url,{
                headers: headers,
                method : 'POST',
                body: JSON.stringify(body)
            })
            .then(response => response.json()) 
            .then(response => this.setState({ 'items' : response})); 
        }

    renderBids() {
        return this.state.items.map(function(item){
            return(
                <div key={item.id} className = "cropList">
                    <Row>
                        <Col xs="2">{item.name}</Col><Col xs="1"></Col><Col xs="3">{item.rating} acres</Col><Col xs="2">{item.biddingPrice}</Col><Col xs="1"></Col><Col xs="1">{item.price} â‚¹</Col><Col xs="1"></Col>
                        <Col xs="1"><button type="submit" id={item.buyerId} className="btn btn-success" onClick={() => this.setState({profile: true, buyer : item.buyerId})}>View Profile</button></Col>
                    </Row><hr/>
                </div>
            )
        })
    }

    render() {
        const {showBidForm} = this.state;
        return (<div>
            <Nav/>
        <div className = "userhomebg">
        <div className="wrapper">
        <div className="main_content">
        </div>
        </div>
        <div style={{'background-image' : 'url(' + logo +')' }} className = "auth-home" >
        <Row>
            <Col>
                {this.renderBids()}
            </Col>
            <Col>
                <br/><h1>Crop Profile:</h1><br/>
                <hr/><Row>
                <CropProfile id = {this.state.cid}
                getPrice = {this.getPrice}/>
                <br/></Row>
                {profile &&
                (<Row>
                PROFILE :DDDD
                {/* <BuyerProfile id = {this.state.buyer}/> */}
                <br/></Row>)}                
            </Col>
        </Row>          
        </div>
        </div>
        </div>
        );
    }
}
