import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './UserHome.css'
import logo from '../images/userhome.jpg';
import '../images/bgimage.css';
import Nav from './nav.js';
import Col from 'react-bootstrap/Col'
import Row from 'react-bootstrap/Row'

var body

class AllCropsBuyer extends Component {
    constructor(props) {
        super(props);
        this.state = {
            'items' : [],
            'id' : this.props.id
        };
    }
    componentDidMount() {
        const url = 'http://localhost:9000/getAllPastBids/'+this.state.id
        console.log(this.state.id)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
            method: 'GET',
            body: JSON.stringify(body)
        })
        .then(response => response.json()) 
        .then(response => this.setState({ 'items' : response}));          
    }

   renderList(buyer){
        return this.state.items.map(function(item){
            console.log("in render list" + buyer);
            return(
                            <div key={item.id} className = "cropList">
                                <Row>
                                    <Col xs="1">{item.name}</Col><Col xs="1"></Col><Col xs="2">{item.area} acres</Col><Col xs="2">{item.location}</Col><Col xs="1"></Col><Col xs="2">{item.price} â‚¹</Col>
                                    <Col xs="3">
                                    {item.status == "bidding" && <button type="submit" id={item.id} className="btn btn-success btn-lg" onClick={() => {window.location.href = "/updateBid/" + item.id + "/" + item.fid + "/" + this.state.buyerid}}>UPDATE BID</button>}
                                    {item.status != "bidding" && <button type="submit" id={item.id} className="btn btn-danger btn-lg" onClick={() => {window.location.href = "/viewBuyer/" + item.id + "/" + this.state.buyerid + "/b"}}>CHECK WINNER</button>}
                                    </Col>
                                </Row><hr/>
                            </div>
                       )
        })
    }

    render(){
        return (
            <div>
            <Nav uid = {this.state.id} role={'buyer'}/>
            <div style={{'background-image' : 'url(' + logo +')' }} className = "auth-home" >
            <div className= "auth-inner-full">
            <Row>
            <h1>Your Past Bids</h1><hr/>
            </Row><hr/>
            <Row>
                <Col xs="1">CROP</Col><Col xs="1"></Col><Col xs="2">AREA</Col><Col xs="2">LOCATION</Col><Col xs="1"></Col><Col xs="2">YOUR BID</Col><Col xs="3">ACTION</Col>
            </Row><hr/>
            <ul>
                {this.renderList(this.state.id)}
            </ul>
            </div>
            </div>
            </div>
        );
    }
}

export default AllCropsBuyer;
