import React, { Component } from "react";
//import { useHistory, withRouter,Link } from "react-router-dom";
import './home.css';
import Navigation from './Nav.js';
import { Container, Row, Col } from 'reactstrap';
import image from '../images/helpdesk.png';
import Collapsible from 'react-collapsible';
import cx from "classnames";
import Collapse from "@kunukn/react-collapse";



export default class About extends Component{
    constructor(props) {
        super(props);
      }
      state = {
        isOpen1: false,
        isOpen2: false,
        isOpen3: false,
        spy3: {}
      };

    render() {
        return (
        <div > <Navigation/>
        <Container>
            <Row>
                <Col><br/>Helpdesk is a portal, or an application which is an integral part of project GPMS Applications.<br/>

       &nbsp; &nbsp;It is designed to provide the general public to log in and submit complaints or any issues regarding application. The complaints raised are sent over to the administrator of the portal and Admin may assign the raised issues to an appropriate member of the management team for resolving the problem.
<br/><br/>
<b>Raise Complaint</b> : The user can use this interface to raise complaint, along with the complaint against, subject of the issue, and contact details.
<br/><br/>
<b>View Complaints </b>: The user can view his complaint and its status under the option 'View Complaints'.
<br/><br/>
Using this portal, User can upload image related to the complaint.
                </Col>

                <Col> <img src={ image } />

                </Col>

            </Row>
<br/>
<br/>
            <Row>
        <Col> <div className="app">
        <button
          className={cx("app__toggle", {
            "app__toggle--active": this.state.isOpen1
          })}
          onClick={() => this.toggle(1)}
        >
          <span className="app__toggle-text">toggle</span>
          <div className="rotate90">
            <svg
              className={cx("icon", { "icon--expanded": this.state.isOpen1 })}
              viewBox="6 0 12 24"
            >
              <polygon points="8 0 6 1.8 14.4 12 6 22.2 8 24 18 12" />
            </svg>
          </div>
        </button>
        <Collapse
          isOpen={this.state.isOpen1}
          className={
            "app__collapse app__collapse--gradient " +
            (this.state.isOpen1 ? "app__collapse--active" : "")
          }
        >
          <div className="app__content">
            Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
            <img
              className="image"
              alt="random"
              src="https://source.unsplash.com/user/erondu/500x200"
            />
            <button onClick={() => this.toggle(1)} className="app__button">
              close
            </button>
          </div>
        </Collapse>

        <button
          className={cx("app__toggle", {
            "app__toggle--active": this.state.isOpen2
          })}
          onClick={() => this.toggle(2)}
        >
          toggle
        </button>

        <Collapse
          isOpen={this.state.isOpen2}
          className={
            "app__collapse app__collapse--gradient " +
            (this.state.isOpen2 ? "app__collapse--active" : "")
          }
          transition="height 800ms cubic-bezier(0.4, 0, 0.2, 1)"
          aria-hidden={this.state.isOpen2 ? "false" : "true"}
          elementType="article"
          render={collapseState => (
            <React.Fragment>
              <div className="app__content">
                <div>{collapseState}</div>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
                eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
                enim ad minim veniam, quis nostrud exercitation ullamco laboris
                nisi ut aliquip ex ea commodo consequat.
                <img
                  className="image"
                  alt="random"
                  src="https://source.unsplash.com/user/erondu/500x200"
                />
                <button onClick={() => this.toggle(2)} className="app__button">
                  close
                </button>
              </div>
            </React.Fragment>
          )}
        />

        <button
          className={cx("app__toggle", {
            "app__toggle--active": this.state.isOpen3
          })}
          onClick={() => this.toggle(3)}
        >
          toggle
        </button>

        <pre style={{ fontSize: "10px", width: "100%" }}>
          {JSON.stringify(this.state.spy3, null, 1)}
        </pre>
        <Collapse
          isOpen={this.state.isOpen3}
          collapseHeight="60px"
          className={
            "app__collapse app__collapse--gradient " +
            (this.state.isOpen3 ? "app__collapse--active" : "")
          }
          onChange={state => this.setState({ spy3: state })}
          render={collapseState => (
            <div className="app__content">
              <div>{collapseState}</div>
              Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
              eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut
              enim ad minim veniam, quis nostrud exercitation ullamco laboris
              nisi ut aliquip ex ea commodo consequat.
            </div>
          )}
        />

        <p className="app__text">Some content below</p>
      </div>
                </Col>
            </Row>
        </Container>
        </div>

        );
    }
    toggle = index => {
        let collapse = "isOpen" + index;

        this.setState(prevState => ({ [collapse]: !prevState[collapse] }));
      };
}
