import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import {
Container
} from 'react-bootstrap'
import Row from 'react-bootstrap/Row'
import './UserHome.css'


class BidsToBuy extends Component {
    constructor(props) {
        super(props);
        this.state = {
        data: null,
        };
    }

    componentDidMount() {
        fetch('http://localhost:9000/recentCrops')
        .then(response => response.json())
        .then(data => this.setState({ data }));
    }

    handleSubmit(event){
        window.location.href  = "/bidding";
    }

    render(){
        return (
            <div class = "recentbids">
                <Container>
                    <Row>
                        {this.state.data}
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 1 &nbsp;
                         <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>

                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 2
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 3
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 4
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>
                    <Row>
                        {this.state.data}
                        Crop 5
                        <inline-button type="submit" className="btn btn-success btn-block" onClick={this.handleSubmit}>Bid</inline-button>
                    </Row>

                </Container>
            </div>
        );
    }
}

export default BidsToBuy;
