import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import './notification.css';
import {
Navbar,
Nav,
NavDropdown,
} from 'react-bootstrap'

var display=false;

class Navigation extends Component {
    constructor(props) {
        super(props);
        this.state ={
            uid : this.props.uid,
            role : this.props.role,
            num : 0,
        }
        this.handleClick = this.handleClick.bind(this);
    }

    componentDidMount(){
        const url = 'http://localhost:9000/getNotifications/'+this.state.uid;
        console.log(this.state.id)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
            method: 'GET',
        })
        .then(response => response.json()) 
        .then(response => this.setState({ 'notifications' : response}));  
    }

    handleClick(){
        display = !display;
    }

    renderNotifications(){
        return this.state.notifications.map(function(notification){
            return(
                <div key={notification.id} className = "cropList">
                    <Row>
                        <Col xs="1">{item.name}</Col><Col xs="1"></Col><Col xs="2">{item.area} acres</Col><Col xs="2">{item.location}</Col><Col xs="1"></Col><Col xs="2">{item.price} ₹</Col>
                        <Col xs="3"><button type="submit" id={item.id} className="btn btn-success btn-lg" onClick={() => {window.location.href = "/viewingTrends/" + item.id}}>VIEW TRENDS</button></Col>
                    </Row><hr/>
                </div>
            )
        })
    }


    render(){
        console.log(this.state.role)
        return (
            <div>
            <Navbar className="navbar-light" expand="md">
                <Navbar.Brand href="/Home"><h3><b>FARMER'S MARKET</b></h3></Navbar.Brand>
                <Navbar.Toggle aria-controls="basic-navbar-nav" />
                <Navbar.Collapse id="basic-navbar-nav">
                    <Nav className="ml-auto">
                        <Nav.Link className="notification" href="/Home">HOME</Nav.Link>
                        {this.state.role== 'admin' && <Nav.Link className="notification" href={"/adminhome/"+this.state.uid}>DASHBOARD</Nav.Link>}
                        {this.state.role== 'farmer' && <Nav.Link className="notification" href={"/userhome/"+this.state.uid}>DASHBOARD</Nav.Link>}
                        {this.state.role== 'buyer' && <Nav.Link className="notification" href={"/buyerhome/"+this.state.uid}>DASHBOARD</Nav.Link>}
                        <Nav.Link className="notification" onClick={this.handleClick()}>
                            NOTIFICATIONS
                            {this.state.num>0 && <span className="redText">({this.state.num})</span>}
                            {this.state.display==true && <div>
                                {this.renderNotifications()}                            
                            </div>}
                        </Nav.Link>
                        <NavDropdown className="notification" title={window.localStorage.getItem("username")} id="basic-nav-dropdown">
                            <NavDropdown.Item href={"/updateProfile/" + this.state.uid}>Update Profile</NavDropdown.Item>
                            <NavDropdown.Item href="/Home" onClick={() => window.sessionStorage.clear() }>Sign out</NavDropdown.Item>
                        </NavDropdown>
                    </Nav>
                </Navbar.Collapse>
            </Navbar>
            </div>
        );
    }
}

export default Navigation;
