import React ,{ Component } from 'react';
import '../../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import {
Container,
} from 'react-bootstrap'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import './UserHome.css'

//var result
class Recents extends Component {
    constructor(props) {
        super(props);
        this.state = {
            cid : '',
            cname : '',
            clocation : '',
            cprice : '',
            fid: this.props.id,
        };
    }

    componentDidMount() {
        const url = 'http://localhost:9000/getfarmercrops/'+this.props.id
        console.log(this.props.fid)
        let headers = new Headers();

        headers.append('Content-Type', 'application/json');
        headers.append('Accept', 'application/json');

        headers.append('Access-Control-Allow-origin', url);
        headers.append('Access-Control-Allow-Credentials', 'true');

        headers.append('POST', 'GET');

        fetch(url,{
            headers: headers,
        })
        .then(response => response.json()) 
        .then(response => this.setState({ 'items' : response}));           
    }

    render(){
        return (
            <div class = "recentbids">
                <ul>
                   {this.state.items.map(function(item){
                       return(
                           <div key={item.id}>
                                <h2>{item.id},{item.name},{item.area},{item.location},{item.price}</h2>
                                <button className="btn btn-success btn-block" onClick="window.location.href = 'http://localhost:9000/getfarmercrops/'+item.id">Bid</button>
                           </div>
                       )
                   })}
               </ul>
            </div>
        );
    }
}

export default Recents;
